import { useEffect, useState } from "react";
import axios from "axios";

const HelloWorld = () => {
    const [message, setMessage] = useState("Loading...");

    const fetchMessage = async () => {
        try {
            const response = await fetch("http://192.168.0.5/message");
            const text = await response.text();
            setMessage(text);
        } catch (error) {
            console.error("Error fetching message:", error);
            setMessage("Failed to load message");
        }
    };
    

    useEffect(() => {
        fetchMessage();
    }, []);

    return (
        <div className="flex justify-center items-center min-h-screen bg-gray-900">
        <div className="w-full max-w-md p-6 bg-gray-800 shadow-lg rounded-2xl text-center">
            <h2 className="text-2xl font-semibold text-white bg-blue-600 py-3 rounded-md">
                ESP8266 Message
            </h2>
            <h3 className="text-lg text-gray-300 mt-4">{message}</h3>
        </div>
    </div>
    );
};

export default HelloWorld;
